var	prototypes	= require("prototypes"),
	Model		= require("models/Model"),
	validators	= require("models/validators");

var Group = function(fields, restrict) {
	fields = fields || {};
	restrict = restrict || {};
	this.constructor.super_.call(this, fields, restrict);
};
Group.inherits(Model);
Group.model = {
	"gid": {
		"type": validators.int(0),
		"required": true
	},
	"name": {
		"type": validators.string(1,64),
		"required": true
	},
	"image": {
		"type": validators.string(0,255)
	},
	"description": {
		"type": validators.string(0,65535)
	},
	
	"owner": {
		"type": validators.object()
	},
	"owner.uid": {
		"type": validators.int(0)
	},
	"owner.name": {
		"type": validators.string(1,32)
	},
	"owner.image": {
		"type": validators.string(0,255)
	},
	
	"members": {
		"type": validators.int(0)
	},
	"date_formed": {
		"type": validators.date()
	}
};

module.exports = exports = Group;