var	util	= require("util");

Function.prototype.inherits = function(super_) {
	if(super_ instanceof Function)
	{
		// Updated to use util.inherits
		util.inherits(this, super_);
		
		// Inherit static functions
		var i;
		for(i in super_)
		{
			if(super_[i] instanceof Function && i!="prototype")
			{
				this[i] = super_[i];
			}
		}
	} else {
		this.prototype = super_;
		this.prototype.constructor = this;
		this.super_ = super_;
	}
};